contract = w3.eth.contract(address=w3.to_checksum_address(bored_ape_addr), abi=bored_ape_abi)
name = contract.functions.name().call()
symbol = contract.functions.symbol().call()
apes = contract.functions.balanceOf(acc_address).call()
print(f"{name} [{symbol}] NFTs in Auctions: {apes}")

ape_price = contract.functions.apePrice().call()
print(f"{name} [{symbol}] Price of an Ape when it was first minted: {ape_price} wei")
price_in_eth = Web3.from_wei(ape_price, 'ether')
print(f"Price of Ape when it was first minted in ETH: {price_in_eth} ETH")

total_apes_supply = contract.functions.totalSupply().call()
print(f"Total Apes in supply: {total_apes_supply}")

max_ape_purchase = contract.functions.maxApePurchase().call()
print(f"The Max Apes that one can purchase: {max_ape_purchase}")

print(f"The Base URI of {symbol} is {contract.functions.baseURI().call()}")

print(f"The owner of Token ID 6168 is {contract.functions.ownerOf(6168).call()}")

